mapper.select:
select R1.* 
from (
     select 
         base_dt ,
         cur_unit ,
         ttb,
         tts,
         deal_bas_r,
         cur_nm
     from exchange
     where cur_unit = #{cur_unit}

) R1
limit #{page_size, jdbcType = INTEGER} offset #{page_no , jdbcType = INTEGER}

mapper.delete:
delete from exchange
where cur_unit = #{curUnit}
and   base_dt = #{baseDt}
